import { useState } from "react";
import productsData from "../data/products";
import ProductCard from "../components/ProductCard";

function Products({ featured }) {
  const [search, setSearch] = useState("");
  const [sort, setSort] = useState("");

  let products = productsData.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  if (sort === "low") products.sort((a, b) => a.price - b.price);
  if (sort === "high") products.sort((a, b) => b.price - a.price);

  if (featured) products = products.slice(0, 3);

  return (
    <div className="px-8 py-16">
      {!featured && (
        <div className="flex flex-wrap gap-4 mb-8">
          <input
            placeholder="Search dresses..."
            className="border px-4 py-2 rounded"
            onChange={(e) => setSearch(e.target.value)}
          />

          <select
            className="border px-4 py-2 rounded"
            onChange={(e) => setSort(e.target.value)}
          >
            <option value="">Sort</option>
            <option value="low">Price: Low â†’ High</option>
            <option value="high">Price: High â†’ Low</option>
          </select>
        </div>
      )}

      {products.length === 0 && <p>No products found ðŸ˜•</p>}
<div className="grid gap-8
                grid-cols-1
                sm:grid-cols-2
                md:grid-cols-3
                lg:grid-cols-4">
  {products.map((p) => (
    <ProductCard key={p.id} product={p} />
  ))}
</div>

    </div>
  );
}

export default Products;
