import { useParams } from "react-router-dom";
import products from "../data/products";
import { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import { motion } from "framer-motion";

function ProductDetails() {
  const { id } = useParams();

  // product id string-aa varum, so Number() IMPORTANT
  const product = products.find((p) => p.id === Number(id));

  const { addToCart } = useContext(CartContext);
  const [qty, setQty] = useState(1);

  if (!product) {
    return <p className="p-10">Product not found</p>;
  }

  const handleAddToCart = () => {
    // ðŸ‘‡ VERY IMPORTANT
    addToCart({
      ...product,
      qty: qty,
    });
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      className="px-8 py-16 max-w-6xl mx-auto"
    >
      <div className="grid md:grid-cols-2 gap-12">
        {/* Image */}
        <div className="overflow-hidden rounded-2xl shadow-lg">
          <img
            src={product.image}
            alt={product.name}
            className="w-full h-[520px] object-cover"
          />
        </div>

        {/* Details */}
        <div>
          <h1 className="text-3xl font-bold">{product.name}</h1>

          <p className="text-primary text-2xl mt-3">
            â‚¹{product.price}
          </p>

          <p className="mt-6 text-gray-600 leading-relaxed">
            {product.description}
          </p>

          {/* Quantity selector */}
          <div className="flex items-center gap-4 mt-8">
            <button
              onClick={() => setQty((q) => Math.max(1, q - 1))}
              className="border px-4 py-2 rounded"
            >
              âˆ’
            </button>

            <span className="text-lg font-medium">{qty}</span>

            <button
              onClick={() => setQty((q) => q + 1)}
              className="border px-4 py-2 rounded"
            >
              +
            </button>
          </div>

          {/* Add to cart */}
          <button
            onClick={handleAddToCart}
            className="mt-8 bg-primary text-white px-10 py-3 rounded-full
                       hover:scale-105 transition"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </motion.div>
  );
}

export default ProductDetails;
