import { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

function Checkout() {
  const { cart } = useContext(CartContext);
  const navigate = useNavigate();
  const [payment, setPayment] = useState("cod");

  const total = cart.reduce(
    (sum, i) => sum + i.price * i.qty,
    0
  );

  const placeOrder = () => {
    // backend later
    navigate("/success");
  };

  return (
    <div className="px-6 py-12 max-w-4xl mx-auto grid md:grid-cols-2 gap-10">
      {/* LEFT – Address */}
      <motion.div
        initial={{ opacity: 0, x: -40 }}
        animate={{ opacity: 1, x: 0 }}
        className="bg-white p-6 rounded-xl shadow"
      >
        <h2 className="text-xl font-semibold mb-4">
          Delivery Address
        </h2>

        <input className="border p-2 w-full mb-3" placeholder="Full Name" />
        <input className="border p-2 w-full mb-3" placeholder="Phone" />
        <input className="border p-2 w-full mb-3" placeholder="Address" />
        <input className="border p-2 w-full mb-3" placeholder="Pincode" />
      </motion.div>

      {/* RIGHT – Payment */}
      <motion.div
        initial={{ opacity: 0, x: 40 }}
        animate={{ opacity: 1, x: 0 }}
        className="bg-white p-6 rounded-xl shadow"
      >
        <h2 className="text-xl font-semibold mb-4">
          Payment Options
        </h2>

        {/* COD */}
        <label className="flex items-center gap-3 mb-3">
          <input
            type="radio"
            checked={payment === "cod"}
            onChange={() => setPayment("cod")}
          />
          Cash on Delivery
        </label>

        {/* Online */}
        <label className="flex items-center gap-3 mb-6">
          <input
            type="radio"
            checked={payment === "online"}
            onChange={() => setPayment("online")}
          />
          Online Payment (UPI / Card)
        </label>

        <div className="flex justify-between font-bold text-lg mb-4">
          <span>Total</span>
          <span className="text-primary">₹{total}</span>
        </div>

        <button
          onClick={placeOrder}
          className="w-full bg-primary text-white py-3 rounded-full hover:scale-105 transition"
        >
          {payment === "cod"
            ? "Place Order"
            : "Pay & Place Order"}
        </button>
      </motion.div>
    </div>
  );
}

export default Checkout;
