import { motion } from "framer-motion";
import { Link } from "react-router-dom";

function Hero() {
  return (
    <div className="grid md:grid-cols-2 items-center px-10 py-20">
      <motion.div
        initial={{ opacity: 0, x: -60 }}
        animate={{ opacity: 1, x: 0 }}
      >
        <h1 className="text-5xl font-bold leading-tight">
          Elevate Your <span className="text-primary">Style</span>
        </h1>
        <p className="mt-6 text-gray-600">
          Premium fashion for modern lifestyle
        </p>

        <Link to="/products">
          <button className="mt-8 bg-primary text-white px-8 py-3 rounded-full hover:scale-105 transition">
            Shop Now
          </button>
        </Link>
      </motion.div>

      <motion.img
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        src="https://images.unsplash.com/photo-1490481651871-ab68de25d43d"
        className="rounded-xl shadow-xl"
      />
    </div>
  );
}

export default Hero;
