import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useContext } from "react";
import { CartContext } from "../context/CartContext";
import { motion } from "framer-motion";

function OrderSuccess() {
  const location = useLocation();
  const navigate = useNavigate();
  const { clearCart } = useContext(CartContext);

  const orderId = location.state?.orderId;

  useEffect(() => {
    // Order ID illa na direct access â€“ redirect
    if (!orderId) {
      navigate("/");
    }

    // Order success apram cart empty pannum
    clearCart();
  }, [orderId, navigate, clearCart]);

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        className="bg-white shadow-xl rounded-2xl p-10 text-center max-w-md w-full"
      >
        <h1 className="text-3xl font-bold text-primary mb-4">
          Order Placed Successfully ðŸŽ‰
        </h1>

        <p className="text-gray-600 mb-2">
          Thank you for shopping with us
        </p>

        <p className="mt-4 font-semibold">
          Order ID:
        </p>
        <p className="text-sm text-gray-500 break-all">
          {orderId}
        </p>

        <Link to="/">
          <button className="mt-8 bg-primary text-white px-8 py-3 rounded-full hover:scale-105 transition">
            Go Home
          </button>
        </Link>
      </motion.div>
    </div>
  );
}

export default OrderSuccess;
