import { useContext } from "react";
import { CartContext } from "../context/CartContext";
import { Link } from "react-router-dom";

function Cart() {
  const { cart, updateQty, removeItem } = useContext(CartContext);

  const total = cart.reduce((s, i) => s + i.price * i.qty, 0);

  if (cart.length === 0)
    return <p className="p-10 text-center">Cart is empty üõí</p>;

  return (
    <div className="px-8 py-16">
      {cart.map((i) => (
        <div key={i.id} className="flex justify-between mb-4">
          <p>{i.name}</p>
          <input
            type="number"
            value={i.qty}
            min="1"
            onChange={(e) => updateQty(i.id, Number(e.target.value))}
            className="w-16 border"
          />
          <button onClick={() => removeItem(i.id)}>‚ùå</button>
        </div>
      ))}

      <h2 className="text-xl font-bold mt-6">Total: ‚Çπ{total}</h2>

      <Link to="/checkout">
        <button className="mt-4 bg-primary text-white px-6 py-2 rounded">
          Checkout
        </button>
      </Link>
    </div>
  );
}

export default Cart;
