import { useParams } from "react-router-dom";
import products from "../data/products";
import { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";

function ProductDetails() {
  const { id } = useParams();
  const product = products.find((p) => p.id === Number(id));
  const { addToCart } = useContext(CartContext);
  const [qty, setQty] = useState(1);

  return (
    <div className="grid md:grid-cols-2 gap-12 px-8 py-16">
      <img src={product.image} className="rounded-xl shadow-lg" />

      <div>
        <h1 className="text-3xl font-bold">{product.name}</h1>
        <p className="text-primary text-2xl mt-2">₹{product.price}</p>
        <p className="mt-4 text-gray-600">{product.description}</p>

        <div className="flex items-center gap-4 mt-6">
          <button onClick={() => setQty(qty - 1)} disabled={qty === 1}>−</button>
          <span>{qty}</span>
          <button onClick={() => setQty(qty + 1)}>+</button>
        </div>

        <button
          onClick={() => addToCart({ ...product, qty })}
          className="mt-6 bg-primary text-white px-8 py-3 rounded-full hover:scale-105 transition"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}

export default ProductDetails;
